<script setup>
import { ref, onMounted, onBeforeUnmount } from 'vue';

function generateBoxShadow(n, max = 2000, color = '#FFF') {
  let arr = [];
  for (let i = 0; i < n; i++) {
    const x = Math.floor(Math.random() * max);
    const y = Math.floor(Math.random() * max);
    arr.push(`${x}px ${y}px ${color}`);
  }
  return arr.join(', ');
}

const starsSmall = ref('');
const starsMedium = ref('');
const starsBig = ref('');
const sectionRef = ref(null);
const avatarVisible = ref(false);

onMounted(() => {
  starsSmall.value = generateBoxShadow(700);
  starsMedium.value = generateBoxShadow(200);
  starsBig.value = generateBoxShadow(100);

  const observer = new IntersectionObserver(
    ([entry]) => {
      // Trigger when bottom of section enters viewport
      if (entry.isIntersecting && entry.intersectionRatio > 0) {
        avatarVisible.value = true;
      }
    },
    {
      threshold: 0, // Trigger as soon as any part is visible
      rootMargin: '0px 0px -10% 0px' // Adjust to trigger near bottom
    }
  );
  if (sectionRef.value) observer.observe(sectionRef.value);

  onBeforeUnmount(() => {
    if (sectionRef.value) observer.unobserve(sectionRef.value);
  });
});
</script>

